import React, { useState, useEffect } from 'react';
import { feedbackAPI, facultyAPI } from '../../services/api';
import { DEPARTMENTS } from '../../utils/constants';
import LoadingSpinner from '../common/LoadingSpinner';

const FeedbackForm = ({ studentName }) => {
  const [formData, setFormData] = useState({
    name: studentName || '',
    department: '',
    rollNumber: '',
    email: '',
    feedback: '',
    rating: '',
    facultyName: ''
  });
  
  const [facultyList, setFacultyList] = useState([]);
  const [loading, setLoading] = useState(false);
  const [submitting, setSubmitting] = useState(false);
  const [message, setMessage] = useState('');

  useEffect(() => {
    fetchFacultyList();
  }, []);

  const fetchFacultyList = async () => {
    try {
      setLoading(true);
      const response = await facultyAPI.getFacultyList();
      setFacultyList(response.data);
    } catch (error) {
      console.error('Error fetching faculty list:', error);
      setMessage('Error loading faculty list');
    } finally {
      setLoading(false);
    }
  };

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setSubmitting(true);
    setMessage('');

    try {
      const response = await feedbackAPI.submitFeedback(formData);
      setMessage(`Feedback submitted successfully! Sentiment: ${response.data.sentiment}`);
      
      // Reset form
      setFormData({
        name: studentName || '',
        department: '',
        rollNumber: '',
        email: '',
        feedback: '',
        rating: '',
        facultyName: ''
      });
    } catch (error) {
      console.error('Error submitting feedback:', error);
      setMessage('Error submitting feedback. Please try again.');
    } finally {
      setSubmitting(false);
    }
  };

  if (loading) {
    return <LoadingSpinner message="Loading form..." />;
  }

  return (
    <div className="container mt-4">
      <div className="row justify-content-center">
        <div className="col-md-8">
          <div className="card">
            <div className="card-header bg-success text-white">
              <h4 className="mb-0">Submit Feedback</h4>
            </div>
            <div className="card-body">
              {message && (
                <div className={`alert ${message.includes('Error') ? 'alert-danger' : 'alert-success'}`}>
                  {message}
                </div>
              )}
              
              <form onSubmit={handleSubmit}>
                <div className="row">
                  <div className="col-md-6 mb-3">
                    <label htmlFor="name" className="form-label">Name *</label>
                    <input
                      type="text"
                      className="form-control"
                      id="name"
                      name="name"
                      value={formData.name}
                      onChange={handleChange}
                      required
                    />
                  </div>
                  
                  <div className="col-md-6 mb-3">
                    <label htmlFor="rollNumber" className="form-label">Roll Number *</label>
                    <input
                      type="text"
                      className="form-control"
                      id="rollNumber"
                      name="rollNumber"
                      value={formData.rollNumber}
                      onChange={handleChange}
                      required
                    />
                  </div>
                </div>

                <div className="row">
                  <div className="col-md-6 mb-3">
                    <label htmlFor="department" className="form-label">Department *</label>
                    <select
                      className="form-select"
                      id="department"
                      name="department"
                      value={formData.department}
                      onChange={handleChange}
                      required
                    >
                      <option value="">Select Department</option>
                      {DEPARTMENTS.map(dept => (
                        <option key={dept} value={dept}>{dept}</option>
                      ))}
                    </select>
                  </div>
                  
                  <div className="col-md-6 mb-3">
                    <label htmlFor="email" className="form-label">Email *</label>
                    <input
                      type="email"
                      className="form-control"
                      id="email"
                      name="email"
                      value={formData.email}
                      onChange={handleChange}
                      required
                    />
                  </div>
                </div>

                <div className="row">
                  <div className="col-md-6 mb-3">
                    <label htmlFor="facultyName" className="form-label">Faculty Name *</label>
                    <select
                      className="form-select"
                      id="facultyName"
                      name="facultyName"
                      value={formData.facultyName}
                      onChange={handleChange}
                      required
                    >
                      <option value="">Select Faculty</option>
                      {facultyList.map(faculty => (
                        <option key={faculty} value={faculty}>{faculty}</option>
                      ))}
                    </select>
                  </div>
                  
                  <div className="col-md-6 mb-3">
                    <label htmlFor="rating" className="form-label">Star Rating *</label>
                    <select
                      className="form-select"
                      id="rating"
                      name="rating"
                      value={formData.rating}
                      onChange={handleChange}
                      required
                    >
                      <option value="">Select Rating</option>
                      <option value="1">1 Star</option>
                      <option value="2">2 Stars</option>
                      <option value="3">3 Stars</option>
                      <option value="4">4 Stars</option>
                      <option value="5">5 Stars</option>
                    </select>
                  </div>
                </div>

                <div className="mb-3">
                  <label htmlFor="feedback" className="form-label">Feedback *</label>
                  <textarea
                    className="form-control"
                    id="feedback"
                    name="feedback"
                    rows="5"
                    value={formData.feedback}
                    onChange={handleChange}
                    placeholder="Please provide your detailed feedback..."
                    required
                  ></textarea>
                </div>

                <button
                  type="submit"
                  className="btn btn-success w-100"
                  disabled={submitting}
                >
                  {submitting ? 'Submitting...' : 'Submit Feedback'}
                </button>
              </form>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default FeedbackForm;
