import React, { useState, useEffect } from 'react';
import { facultyAPI } from '../../services/api';
import LoadingSpinner from '../common/LoadingSpinner';

const FacultyLogin = ({ onLogin }) => {
  const [facultyName, setFacultyName] = useState('');
  const [facultyList, setFacultyList] = useState([]);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    fetchFacultyList();
  }, []);

  const fetchFacultyList = async () => {
    try {
      setLoading(true);
      const response = await facultyAPI.getFacultyList();
      setFacultyList(response.data);
    } catch (error) {
      console.error('Error fetching faculty list:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (facultyName) {
      onLogin(facultyName);
    }
  };

  if (loading) {
    return <LoadingSpinner message="Loading faculty list..." />;
  }

  return (
    <div className="container mt-5">
      <div className="row justify-content-center">
        <div className="col-md-6">
          <div className="card">
            <div className="card-header bg-info text-white">
              <h4 className="mb-0">Faculty Login</h4>
            </div>
            <div className="card-body">
              <form onSubmit={handleSubmit}>
                <div className="mb-3">
                  <label htmlFor="facultyName" className="form-label">
                    Select Your Name
                  </label>
                  <select
                    className="form-select"
                    id="facultyName"
                    value={facultyName}
                    onChange={(e) => setFacultyName(e.target.value)}
                    required
                  >
                    <option value="">Select Faculty</option>
                    {facultyList.map(faculty => (
                      <option key={faculty} value={faculty}>{faculty}</option>
                    ))}
                  </select>
                </div>
                <button type="submit" className="btn btn-info w-100">
                  Continue as Faculty
                </button>
              </form>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default FacultyLogin;
